<?phpclass Controller{	private $base;    function __construct()    {        $this->view = new View();        Session::init();    }    public function loadModel($name)    {		$this->base = $name;        $core = 'core/' . $name . '/model.php';        $plugins = 'plugins/' . $name . '/model.php';        if (file_exists($core)) {			$path = $core;		}else{			$path = $plugins;		}		require $path;		$modelName = $name . "_Model";		$this->model = new $modelName();		$this->view->plugin_name = $name;		$this->view->logo = $this->model->getConfig("display_logo");		$this->view->page = $this->model->page;    }		public function install(){		$this->model->install();	}		public function uninstall(){		$this->model->uninstall();	}}