<?phpclass Session{    public static function init()    {        session_start();    }    public static function set($key, $value)    {        $_SESSION[$key] = $value;    }    public static function get($key)    {        if (isset($_SESSION[$key])) return $_SESSION[$key];        else return false;    }    public static function Unsets($key)    {        if (isset($_SESSION[$key])) {            unset($_SESSION[$key]);            return true;        } else {            return "not set";        }    }    public static function destroy()    {        session_destroy();        unset($_SESSION);    }}?>