<?php/*Publisher: Jamie NicholsDate: 5/21/2014Name: TosType: ModelFunction: Do backend functions for the current pageas well as render the view it's self.*/class Gallery_Model extends Model{ //Define the class as a Model    function __construct()    { //Call the constructor which runs when the class is loaded        parent::__construct();//Call the main controller class constructor    }	function loadImages(){		$sql = "SELECT * FROM plg_gallery_imgs";		$query = $this->db->prepare($sql);		$query->execute();		$imgs = $query->fetchAll(PDO::FETCH_ASSOC);		return $imgs;	}}?>